import sqlite3

DATAPATH = 'component/db/data.db'

def create_database():
    conn = sqlite3.connect(DATAPATH)

    cursor = conn.cursor()

    cursor.execute('''
        CREATE TABLE IF NOT EXISTS utilisateur (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            pseudo TEXT NOT NULL,
            email TEXT NOT NULL,
            mdp TEXT NOT NULL,
            image TEXT NOT NULL
        )
    ''')

    conn.commit()
    conn.close()

def reset_database():
    conn = sqlite3.connect(DATAPATH)
    cursor = conn.cursor()
    cursor.execute('DROP TABLE IF EXISTS utilisateur')
    conn.commit()
    conn.close()

def add_user(pseudo, email, mdp, image):
    conn = sqlite3.connect(DATAPATH)
    cursor = conn.cursor()
    cursor.execute('INSERT INTO utilisateur (pseudo, email, mdp, image) VALUES (?, ?, ?, ?)', (pseudo, email, mdp, image))
    conn.commit()
    conn.close()

def update_user(pseudo, email, mdp, image):
    conn = sqlite3.connect(DATAPATH)
    cursor = conn.cursor()

    cursor.execute('''
        UPDATE utilisateur
        SET pseudo = ?,
            mdp = ?,
            image = ?
        WHERE email = ?;
    ''', (pseudo, email, mdp, image))

    conn.commit()
    conn.close()

reset_database()
create_database()
add_user("administrateur", "administrateur@gmail.com", "12345678", "https://wallpapers-clan.com/wp-content/uploads/2023/11/zelda-link-sword-aesthetic-desktop-wallpaper-preview.jpg")
add_user("pharell", "pharell@gmail.com", "mot de passe", "data:image/jpeg;base64,")
update_user("administrateur", "administrateur@gmail.com", "1234", "https://wallpapers-clan.com/wp-content/uploads/2023/11/zelda-link-sword-aesthetic-desktop-wallpaper-preview.jpg")
